import { EventEmitter } from 'events';
import Coroutine from './coroutine';

declare global {
    interface Window {
        requestIdleCallback: Function;
    }
}

class Engine extends EventEmitter {
    private coroutine: Set<Coroutine>;
    private continues: Function[];

    constructor() {
        super();
        this.coroutine = new Set();
        this.continues = [];

        this.on('add', (cor) => {
            this.coroutine.add(cor);
        })

        this.on('delete', (cor) => {
            this.coroutine.delete(cor);
        })

        this.on('continue', (_continue) => {
            this.continues.push(_continue);
            this.continue();
        })
    }

    private continue = () => {
        const _continue = this.continues.shift();
        if (typeof window !== 'undefined' && window.requestIdleCallback) {
            window.requestIdleCallback(_continue);
        } else {
            _continue();
        }
    }

    public getCoroutines = () => {
        return this.coroutine;
    }
}

export default new Engine();
import engine from './engine';

interface Config {
    name?: string;
    isStart?: boolean;
}

interface CoTools {
    sleep?: Function;
    coSwitch?: Function;
    park: Function;
}

type coBlock = (coTools: CoTools) => Promise<void>;

enum Status {
    NEW = 'NEW',
    RUNNING = 'RUNNING',
    WAITING = 'WAITING',
    DONE = 'DONE'
}

class Coroutine {
    private status: Status;
    private id: string;
    private name: string;
    private coBlock: coBlock;

    static getCoroutines = () => {
        return engine.getCoroutines();
    }

    constructor(coBlock: coBlock, config?: Config) {
        this.status = Status.NEW;
        this.name = config && config.name;
        this.coBlock = coBlock;
        engine.emit('add', this);
        config && config.isStart && this.run();
    }

    public start = () => {
        this.run();
    }

    private run = async () => {
        if (this.status === Status.RUNNING) return;

        this.status = Status.RUNNING;
        const sleep = this.sleep;
        const coSwitch = this.coSwitch;
        const park = this.park;

        await this.coBlock({
            sleep, coSwitch, park
        });

        this.status = Status.DONE;
        engine.emit('delete', this);
    }

    private coSwitch = () => {
        this.status = Status.WAITING;
        return new Promise(resolve => {
            engine.emit('continue', () => {
                resolve();
                this.status = Status.RUNNING;
            })
        });
    }

    private sleep = (t = 0) => {
        this.status = Status.WAITING;
        return new Promise(resolve => {
            setTimeout(() => {
                resolve();
                this.status = Status.RUNNING;
            }, t);
        })
    }

    private park = () => {
        this.status = Status.WAITING;
        return new Promise(() => { });
    }

}

export {
    Coroutine as default,
    Coroutine
}
